package concepts;

public class LoopsConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=5;
		
		while(i<10) {
			System.out.print(i);
			i++;
		}
		
		System.out.println("------------------Reseting i value ------------------------");
		
		i=5;
		
		do {
			System.out.print(i);
			i++;
		}
		while(i<10);
		
		System.out.println("------------------Reseting i value ------------------------");
		
		i=5;
		
		while(i<5) {
			System.out.print(i);
			i++;
		}
		
		
		System.out.println("------------------Reseting i value ------------------------");
		
		i=5;
		
		do {
			System.out.print(i);
			i++;
		}
		while(i<5);
		
		

	
	}

}
